// freemarker语法修复
if (setting.compiler === 'freemarker') {
    content = content
      .replace(/\$\{/g, '<#noparse>${</#noparse>')
      .replace(/\#\{/g, '<#noparse>#{</#noparse>')
}

// login.vue内容替换
if (filename === 'login.vue') {
    content = content
      .replace(/<h2>.*<\/h2>/, '<h2>${loginTitle}</h2>')
      .replace(/<h3>.*<\/h3>/, '<h3>${loginDescription}</h3>')
}

// index.html内容替换
if (filename === 'index.html') {
    content = content
      .replace(/<title>.*<\/title>/, '<title>${loginTitle}</title>')
}

// AppMenu替换菜单顶部标题
if (filename === 'AppMenu.vue') {
    content = content
      .replace('伊娃权限系统', '${menuTitle}')
}

return content
